#include<iostream>#include<algorithm>#include<vector>#include<climits>using namespace std;int main(){    int n,m;    cin>>n>>m;        vector<int> v;    int a;    for(int i=0 ; i<m ; i++){        cin>>a;        v.push_back(a);    }        int diff;    sort(v.begin(),v.end());        vector<int> result;    for(int i=0 ; i<=m-n ; i++){       diff = v[i+n-1] - v[i];       result.push_back(diff);    }        sort(result.begin(),result.end());        cout<<result[0];    return 0;}