#include<iostream>#include<algorithm>#include<vector>#include<climits>using namespace std;int main(){    int n;    cin>>n;        vector<int> v;    int a;    for(int i=0;i<n ;i++){        cin>>a;        v.push_back(a);    }        int count  = 1;    int maxi = 1;    for(int i=0 ; i<n-1 ; i++){        if(v[i+1] >= v[i]){            count++;            maxi = max(maxi,count);        }        else{            count=1;        }    }        cout<<maxi;    return 0;}